import React, { useState, useEffect } from "react";
import { uploadFile, getStatus } from "./api";
import "./App.css";

export default function App() {
  const [file, setFile] = useState(null);
  const [taskId, setTaskId] = useState(null);
  const [status, setStatus] = useState("");
  const [data, setData] = useState([]);
  const [error, setError] = useState("");

  const handleUpload = async () => {
    setError("");
    setData([]);
    setStatus("PENDING");
    try {
      const resp = await uploadFile(file);
      setTaskId(resp.data.task_id);
    } catch (err) {
      setError("Upload failed. Please try again.");
      setStatus("");
    }
  };

  useEffect(() => {
    if (!taskId) return;
    const iv = setInterval(async () => {
      try {
        const resp = await getStatus(taskId);
        const s = resp.data.status;
        setStatus(s);
        if (s === "ERROR") {
          setError(resp.data.message);
          clearInterval(iv);
        }
        if (s === "SUCCESS") {
          setData(resp.data.data);
          clearInterval(iv);
        }
      } catch {
        setError("Error checking task status.");
        clearInterval(iv);
      }
    }, 2000);
    return () => clearInterval(iv);
  }, [taskId]);

  return (
    <div className="app-container">
      <h1 className="app-title">LegalFinePrint</h1>

      <div className="uploader">
        <input
          id="file-input"
          type="file"
          accept=".pdf,.docx,.doc"
          onChange={(e) => setFile(e.target.files[0])}
        />
        <button
          className="btn primary"
          onClick={handleUpload}
          disabled={!file || status === "PENDING"}
        >
          Analyze
        </button>
      </div>

      {status === "PENDING" && (
        <div className="loader-container">
          <div className="loader" />
          <p>Processingâ€¦</p>
        </div>
      )}

      {error && <div className="error-box">{error}</div>}

      {status === "SUCCESS" && (
        <div className="results">
          {data.map((c, i) => {
            const riskClass =
              c.risk_label === "Normal"
                ? "clause low-risk"
                : "clause high-risk";
            return (
              <div key={i} className={riskClass}>
                <div className="clause-header">
                  <span className="risk-label">{c.risk_label}</span>
                </div>
                {c.summary && (
                  <p className="summary">
                    <em>Summary:</em> {c.summary}
                  </p>
                )}
                <p className="clause-text">{c.text}</p>
                <details className="analysis">
                  <summary>Analysis</summary>
                  <pre>{c.analysis}</pre>
                </details>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}
