import React, { useState, useEffect } from "react";
import { uploadFile, getStatus } from "./api";

export default function App() {
  const [file, setFile] = useState(null);
  const [taskId, setTaskId] = useState(null);
  const [status, setStatus] = useState("");
  const [data, setData] = useState([]);
  const [error, setError] = useState("");

  const handleUpload = async () => {
    setError("");
    setData([]);
    setStatus("PENDING");
    const resp = await uploadFile(file);
    setTaskId(resp.data.task_id);
  };

  useEffect(() => {
    if (!taskId) return;
    const iv = setInterval(async () => {
      const resp = await getStatus(taskId);
      const s = resp.data.status;
      setStatus(s);
      if (s === "ERROR") {
        setError(resp.data.message);
        clearInterval(iv);
      }
      if (s === "SUCCESS") {
        setData(resp.data.data);
        clearInterval(iv);
      }
    }, 2000);
    return () => clearInterval(iv);
  }, [taskId]);

  return (
    <div style={{ padding: 20 }}>
      <h1>Legal Document Explainer</h1>
      <input type="file" onChange={e => setFile(e.target.files[0])}/>
      <button onClick={handleUpload} disabled={!file}>Analyze</button>
      {status === "PENDING" && <p>Processingâ€¦</p>}
      {error && <div style={{ color: "red" }}>{error}</div>}
      {status === "SUCCESS" && data.map((c, i) => (
        <div key={i} style={{ margin: "1em 0", padding: "0.5em", border: "1px solid #ccc" }}>
          <strong>[{c.risk_label} | Score: {c.risk_score}]</strong>
          <p>{c.text}</p>
          <details><summary>Analysis</summary><pre>{c.analysis}</pre></details>
        </div>
      ))}
    </div>
  );
}
